import React from 'react';
import './Recipe.css';
import ButtonComponent from './ButtonComponent';

const Recipe =({title,calories,image,ingredients,source,yields,totalWeight,totalNutrients,totalDaily,dietLabels,healthLabels}) => {



    return (
    <div className="eachRecipe">
        <h1>{title}</h1>
        Ingredients:
        <ul> 
            {ingredients.map(ingredient => (
                <li>{ingredient.text}</li>
            ))}

        </ul>
    <p>Source Site: {source}</p>
    <p>Number of Servings: {yields}</p>
    <p>Calorie Count: {calories} kcal</p>
    <p>Total Weight: {totalWeight} g</p>
    <ul> Total Nutrients:
            {totalNutrients.map(totalNutrient => (
                <li>{totalNutrient}</li>
            ))}

        </ul>

        <ul> Total Daily:
            {totalDaily.map(totalDailys => (
                <li>{totalDailys}</li>
            ))}

        </ul>

        <ul> Diet Labels: 
            {dietLabels.map(dietLabel => (
                <li>{dietLabel}</li>
            ))}

        </ul>

        <ul> Health Labels: 
            {healthLabels.map(healthLabel => (
                <li>{healthLabel}</li>
            ))}

        </ul>


    <img src={image} />


    </div>



        );

}

export default Recipe;
