            headers: {
              Accept: "application/json",
              "User-Key": "d45ea1866082729c2bc792dc2a1b4fe3"
            }
          });
        const restdata  = await restd.json();



      }


    return (
    <div className="eachRecipe">
        <h1>{title}</h1>
        <ol>
            {ingredients.map(ingredient => (
                <li>{ingredient.text}</li>
            ))}

        </ol>
    <p>{calories}</p>
    <img src={image} />
    </div>



        );
}

export default Recipe;
