import React from 'react';
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form';

class App extends React.Component {



    onFormSubmit = (event) => {




        event.preventDefault();
        console.log(this.input.value);



    };



    render(){
      return(
        <div>

<Form onSubmit={this.onFormSubmit}>

  <Form.Group  controlId="q">
    <Form.Label>What is your favorite food:</Form.Label>
    <Form.Control type="text" placeholder="Enter food" inputRef={ref => { this.input = ref; }}/>
  </Form.Group>

  <Form.Group controlId="formBasicEmail">
    <Form.Label>What diet do you need:</Form.Label>
    <Form.Control type="text" placeholder="Enter diet" />
  </Form.Group>

  <Form.Group controlId="formBasicEmail">
    <Form.Label>Do you want food to be free of something:</Form.Label>
    <Form.Control type="text" placeholder="Enter food" />
  </Form.Group>

  <Form.Group controlId="formBasicEmail">
    <Form.Label>What is the meal type:</Form.Label>
    <Form.Control type="text" placeholder="Enter meal" />
  </Form.Group>

  <Form.Group controlId="formBasicEmail">
    <Form.Label>Any particular dish type:</Form.Label>
    <Form.Control type="text" placeholder="Enter dish" />
  </Form.Group>

  <Form.Group controlId="formBasicEmail">
    <Form.Label>Do you want to exclude any food:</Form.Label>
    <Form.Control type="text" placeholder="Enter food" />
  </Form.Group>

  <Form.Group controlId="formBasicEmail">
    <Form.Label>Max clories intake:</Form.Label>
    <Form.Control type="number" placeholder="Enter calorie" />
  </Form.Group>

  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>


        </div>
    );
    }
  }


export default App;
